contracts/core/PixBrlaExchange.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../tokens/BRLAToken.sol";
import "../oracles/PixOracle.sol";

/**
 * @title PixBrlaExchange
 * @dev Contrato principal para conversão PIX <-> BRLA
 * @author Felipe Bercio
 */
contract PixBrlaExchange is Ownable, ReentrancyGuard, Pausable {

    // Interfaces
    BRLAToken public immutable brlaToken;
    PixOracle public pixOracle;

    // Configurações
    uint256 public constant CONVERSION_RATE = 1e18; // 1 PIX = 1 BRLA (1:1)
    uint256 public feePercentage = 50; // 0.5% (50/10000)
    uint256 public constant FEE_DENOMINATOR = 10000;

    // Limites
    uint256 public minPixAmount = 1e18; // 1 BRL mínimo
    uint256 public maxPixAmount = 10000e18; // 10.000 BRL máximo
    uint256 public dailyLimit = 50000e18; // 50.000 BRL por dia

    // Endereços importantes
    address public treasury;
    address public feeCollector;

    // Controle de limites diários
    mapping(address => uint256) public dailyPixTobrlaVolume;
    mapping(address => uint256) public dailyBrlaToPixVolume;
    mapping(address => uint256) public lastTransactionDay;

    // Controle de transações
    mapping(string => bool) public processedPixIds;
    mapping(address => uint256) public userNonce;

    // Eventos
    event PixToBrlaInitiated(
        string indexed pixId,
        address indexed user,
        uint256 pixAmount,
        uint256 expectedBrlaAmount,
        uint256 nonce
    );

    event PixToBrlaCompleted(
        string indexed pixId,
        address indexed user,
        uint256 pixAmount,
        uint256 brlaAmount,
        uint256 fee
    );

    event BrlaToPixInitiated(
        address indexed user,
        uint256 brlaAmount,
        uint256 expectedPixAmount,
        string pixKey,
        uint256 nonce
    );

    event BrlaToPixCompleted(
        address indexed user,
        uint256 brlaAmount,
        uint256 pixAmount,
        uint256 fee,
        string pixKey
    );

    event ConfigurationUpdated(
        uint256 feePercentage,
        uint256 minPixAmount,
        uint256 maxPixAmount,
        uint256 dailyLimit
    );

    event AddressesUpdated(
        address treasury,
        address feeCollector,
        address pixOracle
    );

    constructor(
        address _brlaToken,
        address _pixOracle,
        address _treasury,
        address _feeCollector,
        address initialOwner
    ) Ownable(initialOwner) {
        require(_brlaToken != address(0), "Invalid BRLA token address");
        require(_pixOracle != address(0), "Invalid PIX oracle address");
        require(_treasury != address(0), "Invalid treasury address");
        require(_feeCollector != address(0), "Invalid fee collector address");

        brlaToken = BRLAToken(_brlaToken);
        pixOracle = PixOracle(_pixOracle);
        treasury = _treasury;
        feeCollector = _feeCollector;
    }

    /**
     * @dev Iniciar conversão PIX para BRLA
     * @param pixId ID único da transação PIX
     * @param expectedAmount Valor esperado em PIX
     */
    function initiatePixToBrla(
        string calldata pixId,
        uint256 expectedAmount
    ) external nonReentrant whenNotPaused {
        require(bytes(pixId).length > 0, "PIX ID cannot be empty");
        require(!processedPixIds[pixId], "PIX ID already processed");
        require(expectedAmount >= minPixAmount, "Amount below minimum");
        require(expectedAmount <= maxPixAmount, "Amount exceeds maximum");

        // Verificar limite diário
        _checkDailyLimit(msg.sender, expectedAmount, true);

        // Marcar como processado
        processedPixIds[pixId] = true;

        // Incrementar nonce do usuário
        userNonce[msg.sender]++;

        // Calcular BRLA esperado
        uint256 expectedBrlaAmount = (expectedAmount * CONVERSION_RATE) / 1e18;

        emit PixToBrlaInitiated(
            pixId,
            msg.sender,
            expectedAmount,
            expectedBrlaAmount,
            userNonce[msg.sender]
        );
    }

    /**
     * @dev Completar conversão PIX para BRLA (chamado pelo oracle)
     * @param pixId ID da transação PIX
     */
    function completePixToBrla(string calldata pixId) external nonReentrant {
        require(
            pixOracle.isReporter(msg.sender),
            "Only authorized reporters can complete"
        );
        require(
            pixOracle.isPixPaymentConfirmed(pixId),
            "PIX payment not confirmed"
        );

        // Obter informações do pagamento
        (
            uint256 pixAmount,
            address recipient,
            bool confirmed,
            ,
            ,
        ) = pixOracle.getPixPaymentInfo(pixId);

        require(confirmed, "Payment not confirmed");
        require(pixAmount > 0, "Invalid PIX amount");
        require(recipient != address(0), "Invalid recipient");

        // Calcular valores
        uint256 fee = (pixAmount * feePercentage) / FEE_DENOMINATOR;
        uint256 netAmount = pixAmount - fee;
        uint256 brlaAmount = (netAmount * CONVERSION_RATE) / 1e18;

        // Mint BRLA para o usuário
        brlaToken.mint(recipient, brlaAmount);

        // Mint taxa para o collector
        if (fee > 0) {
            uint256 feeInBrla = (fee * CONVERSION_RATE) / 1e18;
            brlaToken.mint(feeCollector, feeInBrla);
        }

        emit PixToBrlaCompleted(pixId, recipient, pixAmount, brlaAmount, fee);
    }

    /**
     * @dev Iniciar conversão BRLA para PIX
     * @param brlaAmount Quantidade de BRLA a ser convertida
     * @param pixKey Chave PIX de destino
     */
    function initiateBrlaToPixConversion(
        uint256 brlaAmount,
        string calldata pixKey
    ) external nonReentrant whenNotPaused {
        require(brlaAmount > 0, "Amount must be greater than 0");
        require(bytes(pixKey).length > 0, "PIX key cannot be empty");
        require(
            brlaToken.balanceOf(msg.sender) >= brlaAmount,
            "Insufficient BRLA balance"
        );

        // Converter para valor PIX
        uint256 pixAmount = (brlaAmount * 1e18) / CONVERSION_RATE;
        require(pixAmount >= minPixAmount, "Amount below minimum");
        require(pixAmount <= maxPixAmount, "Amount exceeds maximum");

        // Verificar limite diário
        _checkDailyLimit(msg.sender, pixAmount, false);

        // Calcular taxa
        uint256 fee = (pixAmount * feePercentage) / FEE_DENOMINATOR;
        uint256 netPixAmount = pixAmount - fee;

        // Burn BRLA do usuário
        brlaToken.burnFrom(msg.sender, brlaAmount);

        // Mint taxa para o collector
        if (fee > 0) {
            uint256 feeInBrla = (fee * CONVERSION_RATE) / 1e18;
            brlaToken.mint(feeCollector, feeInBrla);
        }

        // Incrementar nonce do usuário
        userNonce[msg.sender]++;

        emit BrlaToPixInitiated(
            msg.sender,
            brlaAmount,
            netPixAmount,
            pixKey,
            userNonce[msg.sender]
        );

        emit BrlaToPixCompleted(
            msg.sender,
            brlaAmount,
            netPixAmount,
            fee,
            pixKey
        );
    }

    /**
     * @dev Verificar limite diário
     */
    function _checkDailyLimit(address user, uint256 amount, bool isPixToBrla) internal {
        uint256 currentDay = block.timestamp / 1 days;

        if (lastTransactionDay[user] != currentDay) {
            dailyPixTobrlaVolume[user] = 0;
            dailyBrlaToPixVolume[user] = 0;
            lastTransactionDay[user] = currentDay;
        }

        if (isPixToBrla) {
            require(
                dailyPixTobrlaVolume[user] + amount <= dailyLimit,
                "Daily PIX to BRLA limit exceeded"
            );
            dailyPixTobrlaVolume[user] += amount;
        } else {
            require(
                dailyBrlaToPixVolume[user] + amount <= dailyLimit,
                "Daily BRLA to PIX limit exceeded"
            );
            dailyBrlaToPixVolume[user] += amount;
        }
    }

    // Funções administrativas
    function updateConfiguration(
        uint256 _feePercentage,
        uint256 _minPixAmount,
        uint256 _maxPixAmount,
        uint256 _dailyLimit
    ) external onlyOwner {
        require(_feePercentage <= 1000, "Fee cannot exceed 10%");
        require(_minPixAmount > 0, "Min amount must be greater than 0");
        require(_maxPixAmount > _minPixAmount, "Max amount must be greater than min");
        require(_dailyLimit > _maxPixAmount, "Daily limit must be greater than max amount");

        feePercentage = _feePercentage;
        minPixAmount = _minPixAmount;
        maxPixAmount = _maxPixAmount;
        dailyLimit = _dailyLimit;

        emit ConfigurationUpdated(_feePercentage, _minPixAmount, _maxPixAmount, _dailyLimit);
    }

    function updateAddresses(
        address _treasury,
        address _feeCollector,
        address _pixOracle
    ) external onlyOwner {
        require(_treasury != address(0), "Invalid treasury address");
        require(_feeCollector != address(0), "Invalid fee collector address");
        require(_pixOracle != address(0), "Invalid PIX oracle address");

        treasury = _treasury;
        feeCollector = _feeCollector;
        pixOracle = PixOracle(_pixOracle);

        emit AddressesUpdated(_treasury, _feeCollector, _pixOracle);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // Funções de visualização
    function getUserDailyLimits(address user) external view returns (
        uint256 pixToBrlaUsed,
        uint256 brlaToPixUsed,
        uint256 remainingLimit
    ) {
        uint256 currentDay = block.timestamp / 1 days;

        if (lastTransactionDay[user] == currentDay) {
            pixToBrlaUsed = dailyPixTobrlaVolume[user];
            brlaToPixUsed = dailyBrlaToPixVolume[user];
        } else {
            pixToBrlaUsed = 0;
            brlaToPixUsed = 0;
        }

        uint256 totalUsed = pixToBrlaUsed + brlaToPixUsed;
        remainingLimit = totalUsed >= dailyLimit ? 0 : dailyLimit - totalUsed;
    }

    function calculateFee(uint256 amount) external view returns (uint256) {
        return (amount * feePercentage) / FEE_DENOMINATOR;
    }

    function getConversionRate() external pure returns (uint256) {
        return CONVERSION_RATE;
    }

    function getUserNonce(address user) external view returns (uint256) {
        return userNonce[user];
    }
}

contracts/oracles/PixOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title PixOracle
 * @dev Oracle para validação de pagamentos PIX
 * @author Felipe Bercio
 */
contract PixOracle is Ownable, ReentrancyGuard, AccessControl {
    bytes32 public constant REPORTER_ROLE = keccak256("REPORTER_ROLE");

    struct PixPayment {
        uint256 amount;          // Valor em wei (BRL * 10^18)
        address recipient;       // Endereço do destinatário na blockchain
        bool confirmed;          // Status de confirmação
        uint256 timestamp;       // Timestamp da confirmação
        string pixKey;          // Chave PIX utilizada
        string bankTransactionId; // ID da transação no banco
    }

    // Mapeamentos
    mapping(string => PixPayment) public pixPayments;
    mapping(string => bool) public processedTransactions;

    // Eventos
    event PixPaymentReported(
        string indexed pixId,
        uint256 amount,
        address indexed recipient,
        string pixKey,
        string bankTransactionId
    );

    event PixPaymentConfirmed(
        string indexed pixId,
        uint256 amount,
        address indexed recipient
    );

    event ReporterAdded(address indexed reporter);
    event ReporterRemoved(address indexed reporter);

    constructor(address initialOwner) Ownable(initialOwner) {
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(REPORTER_ROLE, initialOwner);
    }

    /**
     * @dev Reportar pagamento PIX recebido
     * @param pixId ID único da transação PIX
     * @param amount Valor em wei (BRL * 10^18)
     * @param recipient Endereço do destinatário
     * @param pixKey Chave PIX utilizada
     * @param bankTransactionId ID da transação no banco
     */
    function reportPixPayment(
        string calldata pixId,
        uint256 amount,
        address recipient,
        string calldata pixKey,
        string calldata bankTransactionId
    ) external onlyRole(REPORTER_ROLE) nonReentrant {
        require(bytes(pixId).length > 0, "PIX ID cannot be empty");
        require(amount > 0, "Amount must be greater than 0");
        require(recipient != address(0), "Invalid recipient address");
        require(!processedTransactions[pixId], "Transaction already processed");

        // Registrar pagamento
        pixPayments[pixId] = PixPayment({
            amount: amount,
            recipient: recipient,
            confirmed: false,
            timestamp: block.timestamp,
            pixKey: pixKey,
            bankTransactionId: bankTransactionId
        });

        processedTransactions[pixId] = true;

        emit PixPaymentReported(pixId, amount, recipient, pixKey, bankTransactionId);
    }

    /**
     * @dev Confirmar pagamento PIX
     * @param pixId ID da transação PIX
     */
    function confirmPixPayment(string calldata pixId) 
        external 
        onlyRole(REPORTER_ROLE) 
        nonReentrant 
        returns (bool) 
    {
        require(bytes(pixId).length > 0, "PIX ID cannot be empty");
        require(processedTransactions[pixId], "Transaction not reported");
        require(!pixPayments[pixId].confirmed, "Payment already confirmed");

        pixPayments[pixId].confirmed = true;

        emit PixPaymentConfirmed(
            pixId, 
            pixPayments[pixId].amount, 
            pixPayments[pixId].recipient
        );

        return true;
    }

    /**
     * @dev Verificar se pagamento foi confirmado
     */
    function isPixPaymentConfirmed(string calldata pixId) 
        external 
        view 
        returns (bool) 
    {
        return pixPayments[pixId].confirmed;
    }

    /**
     * @dev Obter informações do pagamento
     */
    function getPixPaymentInfo(string calldata pixId) 
        external 
        view 
        returns (
            uint256 amount,
            address recipient,
            bool confirmed,
            uint256 timestamp,
            string memory pixKey,
            string memory bankTransactionId
        ) 
    {
        PixPayment memory payment = pixPayments[pixId];
        return (
            payment.amount,
            payment.recipient,
            payment.confirmed,
            payment.timestamp,
            payment.pixKey,
            payment.bankTransactionId
        );
    }

    /**
     * @dev Adicionar reporter
     */
    function addReporter(address reporter) external onlyOwner {
        require(reporter != address(0), "Invalid reporter address");
        grantRole(REPORTER_ROLE, reporter);
        emit ReporterAdded(reporter);
    }

    /**
     * @dev Remover reporter
     */
    function removeReporter(address reporter) external onlyOwner {
        revokeRole(REPORTER_ROLE, reporter);
        emit ReporterRemoved(reporter);
    }

    /**
     * @dev Verificar se endereço é reporter
     */
    function isReporter(address account) external view returns (bool) {
        return hasRole(REPORTER_ROLE, account);
    }
}

contracts/PixStablecoinExchange.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title PixStablecoinExchange
 * @dev Contrato para conversão instantânea PIX -> Stablecoins (BRLA, USDC)
 * @author Felipe Bercio
 * @notice Permite que usuários façam PIX e recebam stablecoins instantaneamente
 */
contract PixStablecoinExchange is Ownable, ReentrancyGuard, Pausable, AccessControl {
    using SafeERC20 for IERC20;
    using Math for uint256;

    // Roles
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // Supported stablecoins
    struct StablecoinInfo {
        IERC20 token;
        uint8 decimals;
        bool isActive;
        uint256 minAmount;
        uint256 maxAmount;
        uint256 dailyLimit;
        uint256 poolBalance;
        uint256 exchangeRate; // Rate in relation to BRL (1e18 = 1:1)
    }

    // PIX transaction structure
    struct PixTransaction {
        string pixId;
        address user;
        uint256 pixAmount; // Amount in BRL (wei format)
        address stablecoin;
        uint256 stablecoinAmount;
        uint256 fee;
        uint256 timestamp;
        TransactionStatus status;
        string pixKey;
        string bankTransactionId;
    }

    enum TransactionStatus {
        INITIATED,
        PIX_CONFIRMED,
        COMPLETED,
        FAILED,
        REFUNDED
    }

    // State variables
    mapping(address => StablecoinInfo) public stablecoins;
    mapping(string => PixTransaction) public pixTransactions;
    mapping(address => mapping(uint256 => uint256)) public dailyVolume; // user -> day -> volume
    mapping(address => uint256) public userNonce;

    address[] public supportedStablecoins;

    // Configuration
    uint256 public feePercentage = 50; // 0.5% (50/10000)
    uint256 public constant FEE_DENOMINATOR = 10000;
    uint256 public constant MAX_FEE_PERCENTAGE = 1000; // 10%

    address public treasury;
    address public feeCollector;

    // Events
    event PixToStablecoinInitiated(
        string indexed pixId,
        address indexed user,
        uint256 pixAmount,
        address indexed stablecoin,
        uint256 expectedStablecoinAmount,
        uint256 nonce
    );

    event PixConfirmed(
        string indexed pixId,
        address indexed user,
        uint256 pixAmount,
        string bankTransactionId
    );

    event StablecoinTransferred(
        string indexed pixId,
        address indexed user,
        address indexed stablecoin,
        uint256 stablecoinAmount,
        uint256 fee
    );

    event StablecoinToPixInitiated(
        address indexed user,
        address indexed stablecoin,
        uint256 stablecoinAmount,
        uint256 expectedPixAmount,
        string pixKey,
        uint256 nonce
    );

    event StablecoinAdded(
        address indexed stablecoin,
        uint8 decimals,
        uint256 minAmount,
        uint256 maxAmount,
        uint256 dailyLimit
    );

    event StablecoinUpdated(
        address indexed stablecoin,
        uint256 minAmount,
        uint256 maxAmount,
        uint256 dailyLimit,
        uint256 exchangeRate
    );

    event PoolFunded(
        address indexed stablecoin,
        uint256 amount,
        address indexed funder
    );

    event PoolWithdrawn(
        address indexed stablecoin,
        uint256 amount,
        address indexed recipient
    );

    constructor(
        address _treasury,
        address _feeCollector,
        address _initialOwner
    ) Ownable(_initialOwner) {
        require(_treasury != address(0), "Invalid treasury address");
        require(_feeCollector != address(0), "Invalid fee collector address");

        treasury = _treasury;
        feeCollector = _feeCollector;

        _grantRole(DEFAULT_ADMIN_ROLE, _initialOwner);
        _grantRole(ORACLE_ROLE, _initialOwner);
        _grantRole(OPERATOR_ROLE, _initialOwner);
        _grantRole(PAUSER_ROLE, _initialOwner);
    }

    /**
     * @dev Adicionar nova stablecoin suportada
     */
    function addStablecoin(
        address _token,
        uint8 _decimals,
        uint256 _minAmount,
        uint256 _maxAmount,
        uint256 _dailyLimit,
        uint256 _exchangeRate
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_token != address(0), "Invalid token address");
        require(_maxAmount > _minAmount, "Max amount must be greater than min");
        require(_dailyLimit > _maxAmount, "Daily limit must be greater than max");
        require(_exchangeRate > 0, "Exchange rate must be greater than 0");
        require(!stablecoins[_token].isActive, "Stablecoin already exists");

        stablecoins[_token] = StablecoinInfo({
            token: IERC20(_token),
            decimals: _decimals,
            isActive: true,
            minAmount: _minAmount,
            maxAmount: _maxAmount,
            dailyLimit: _dailyLimit,
            poolBalance: 0,
            exchangeRate: _exchangeRate
        });

        supportedStablecoins.push(_token);

        emit StablecoinAdded(_token, _decimals, _minAmount, _maxAmount, _dailyLimit);
    }

    /**
     * @dev Iniciar conversão PIX para Stablecoin
     */
    function initiatePixToStablecoin(
        string calldata _pixId,
        uint256 _pixAmount,
        address _stablecoin
    ) external nonReentrant whenNotPaused {
        require(bytes(_pixId).length > 0, "PIX ID cannot be empty");
        require(_pixAmount > 0, "PIX amount must be greater than 0");
        require(stablecoins[_stablecoin].isActive, "Stablecoin not supported");
        require(pixTransactions[_pixId].user == address(0), "PIX ID already exists");

        StablecoinInfo storage stablecoinInfo = stablecoins[_stablecoin];

        // Verificar limites
        require(_pixAmount >= stablecoinInfo.minAmount, "Amount below minimum");
        require(_pixAmount <= stablecoinInfo.maxAmount, "Amount exceeds maximum");

        // Verificar limite diário
        _checkDailyLimit(msg.sender, _pixAmount);

        // Calcular quantidade de stablecoin esperada
        uint256 expectedStablecoinAmount = _calculateStablecoinAmount(
            _pixAmount,
            _stablecoin
        );

        // Verificar se há liquidez suficiente
        require(
            stablecoinInfo.poolBalance >= expectedStablecoinAmount,
            "Insufficient pool liquidity"
        );

        // Criar transação
        pixTransactions[_pixId] = PixTransaction({
            pixId: _pixId,
            user: msg.sender,
            pixAmount: _pixAmount,
            stablecoin: _stablecoin,
            stablecoinAmount: expectedStablecoinAmount,
            fee: _calculateFee(_pixAmount),
            timestamp: block.timestamp,
            status: TransactionStatus.INITIATED,
            pixKey: "",
            bankTransactionId: ""
        });

        // Incrementar nonce do usuário
        userNonce[msg.sender]++;

        emit PixToStablecoinInitiated(
            _pixId,
            msg.sender,
            _pixAmount,
            _stablecoin,
            expectedStablecoinAmount,
            userNonce[msg.sender]
        );
    }

    /**
     * @dev Confirmar recebimento do PIX (chamado pelo oracle)
     */
    function confirmPixPayment(
        string calldata _pixId,
        string calldata _bankTransactionId,
        string calldata _pixKey
    ) external onlyRole(ORACLE_ROLE) nonReentrant {
        PixTransaction storage transaction = pixTransactions[_pixId];
        require(transaction.user != address(0), "Transaction not found");
        require(
            transaction.status == TransactionStatus.INITIATED,
            "Transaction already processed"
        );

        // Atualizar informações da transação
        transaction.status = TransactionStatus.PIX_CONFIRMED;
        transaction.bankTransactionId = _bankTransactionId;
        transaction.pixKey = _pixKey;

        emit PixConfirmed(_pixId, transaction.user, transaction.pixAmount, _bankTransactionId);

        // Processar transferência de stablecoin automaticamente
        _processStablecoinTransfer(_pixId);
    }

    /**
     * @dev Processar transferência de stablecoin
     */
    function _processStablecoinTransfer(string memory _pixId) internal {
        PixTransaction storage transaction = pixTransactions[_pixId];
        require(
            transaction.status == TransactionStatus.PIX_CONFIRMED,
            "PIX not confirmed"
        );

        StablecoinInfo storage stablecoinInfo = stablecoins[transaction.stablecoin];

        // Calcular valores finais
        uint256 fee = transaction.fee;
        uint256 netStablecoinAmount = transaction.stablecoinAmount;

        // Verificar liquidez novamente
        require(
            stablecoinInfo.poolBalance >= netStablecoinAmount,
            "Insufficient pool liquidity"
        );

        // Transferir stablecoin para o usuário
        stablecoinInfo.token.safeTransfer(transaction.user, netStablecoinAmount);

        // Atualizar saldo do pool
        stablecoinInfo.poolBalance -= netStablecoinAmount;

        // Transferir taxa (em stablecoin)
        if (fee > 0) {
            uint256 feeInStablecoin = _calculateStablecoinAmount(fee, transaction.stablecoin);
            if (stablecoinInfo.poolBalance >= feeInStablecoin) {
                stablecoinInfo.token.safeTransfer(feeCollector, feeInStablecoin);
                stablecoinInfo.poolBalance -= feeInStablecoin;
            }
        }

        // Atualizar volume diário
        uint256 currentDay = block.timestamp / 1 days;
        dailyVolume[transaction.user][currentDay] += transaction.pixAmount;

        // Atualizar status
        transaction.status = TransactionStatus.COMPLETED;

        emit StablecoinTransferred(
            _pixId,
            transaction.user,
            transaction.stablecoin,
            netStablecoinAmount,
            fee
        );
    }

    /**
     * @dev Iniciar conversão Stablecoin para PIX
     */
    function initiateStablecoinToPix(
        address _stablecoin,
        uint256 _stablecoinAmount,
        string calldata _pixKey
    ) external nonReentrant whenNotPaused {
        require(stablecoins[_stablecoin].isActive, "Stablecoin not supported");
        require(_stablecoinAmount > 0, "Amount must be greater than 0");
        require(bytes(_pixKey).length > 0, "PIX key cannot be empty");

        StablecoinInfo storage stablecoinInfo = stablecoins[_stablecoin];

        // Verificar saldo do usuário
        require(
            stablecoinInfo.token.balanceOf(msg.sender) >= _stablecoinAmount,
            "Insufficient stablecoin balance"
        );

        // Calcular valor PIX esperado
        uint256 expectedPixAmount = _calculatePixAmount(_stablecoinAmount, _stablecoin);

        // Verificar limites
        require(expectedPixAmount >= stablecoinInfo.minAmount, "Amount below minimum");
        require(expectedPixAmount <= stablecoinInfo.maxAmount, "Amount exceeds maximum");

        // Verificar limite diário
        _checkDailyLimit(msg.sender, expectedPixAmount);

        // Transferir stablecoin para o pool
        stablecoinInfo.token.safeTransferFrom(
            msg.sender,
            address(this),
            _stablecoinAmount
        );

        // Atualizar saldo do pool
        stablecoinInfo.poolBalance += _stablecoinAmount;

        // Calcular taxa
        uint256 fee = _calculateFee(expectedPixAmount);
        uint256 netPixAmount = expectedPixAmount - fee;

        // Incrementar nonce do usuário
        userNonce[msg.sender]++;

        // Atualizar volume diário
        uint256 currentDay = block.timestamp / 1 days;
        dailyVolume[msg.sender][currentDay] += expectedPixAmount;

        emit StablecoinToPixInitiated(
            msg.sender,
            _stablecoin,
            _stablecoinAmount,
            netPixAmount,
            _pixKey,
            userNonce[msg.sender]
        );
    }

    /**
     * @dev Calcular quantidade de stablecoin baseada no valor PIX
     */
    function _calculateStablecoinAmount(
        uint256 _pixAmount,
        address _stablecoin
    ) internal view returns (uint256) {
        StablecoinInfo storage stablecoinInfo = stablecoins[_stablecoin];

        // Ajustar para decimais da stablecoin
        uint256 amount = (_pixAmount * stablecoinInfo.exchangeRate) / 1e18;

        // Ajustar para decimais corretos
        if (stablecoinInfo.decimals != 18) {
            amount = (amount * (10 ** stablecoinInfo.decimals)) / 1e18;
        }

        return amount;
    }

    /**
     * @dev Calcular valor PIX baseado na quantidade de stablecoin
     */
    function _calculatePixAmount(
        uint256 _stablecoinAmount,
        address _stablecoin
    ) internal view returns (uint256) {
        StablecoinInfo storage stablecoinInfo = stablecoins[_stablecoin];

        // Ajustar para 18 decimais se necessário
        uint256 normalizedAmount = _stablecoinAmount;
        if (stablecoinInfo.decimals != 18) {
            normalizedAmount = (_stablecoinAmount * 1e18) / (10 ** stablecoinInfo.decimals);
        }

        // Calcular valor PIX
        return (normalizedAmount * 1e18) / stablecoinInfo.exchangeRate;
    }

    /**
     * @dev Calcular taxa
     */
    function _calculateFee(uint256 _amount) internal view returns (uint256) {
        return (_amount * feePercentage) / FEE_DENOMINATOR;
    }

    /**
     * @dev Verificar limite diário
     */
    function _checkDailyLimit(address _user, uint256 _amount) internal view {
        uint256 currentDay = block.timestamp / 1 days;
        uint256 currentDailyVolume = dailyVolume[_user][currentDay];

        // Verificar limite diário baseado na primeira stablecoin ativa
        for (uint256 i = 0; i < supportedStablecoins.length; i++) {
            address stablecoin = supportedStablecoins[i];
            if (stablecoins[stablecoin].isActive) {
                require(
                    currentDailyVolume + _amount <= stablecoins[stablecoin].dailyLimit,
                    "Daily limit exceeded"
                );
                break;
            }
        }
    }

    /**
     * @dev Financiar pool de stablecoin
     */
    function fundPool(
        address _stablecoin,
        uint256 _amount
    ) external onlyRole(OPERATOR_ROLE) {
        require(stablecoins[_stablecoin].isActive, "Stablecoin not supported");
        require(_amount > 0, "Amount must be greater than 0");

        StablecoinInfo storage stablecoinInfo = stablecoins[_stablecoin];

        stablecoinInfo.token.safeTransferFrom(msg.sender, address(this), _amount);
        stablecoinInfo.poolBalance += _amount;

        emit PoolFunded(_stablecoin, _amount, msg.sender);
    }

    /**
     * @dev Retirar do pool de stablecoin
     */
    function withdrawFromPool(
        address _stablecoin,
        uint256 _amount,
        address _recipient
    ) external onlyRole(OPERATOR_ROLE) {
        require(stablecoins[_stablecoin].isActive, "Stablecoin not supported");
        require(_amount > 0, "Amount must be greater than 0");
        require(_recipient != address(0), "Invalid recipient");

        StablecoinInfo storage stablecoinInfo = stablecoins[_stablecoin];
        require(stablecoinInfo.poolBalance >= _amount, "Insufficient pool balance");

        stablecoinInfo.token.safeTransfer(_recipient, _amount);
        stablecoinInfo.poolBalance -= _amount;

        emit PoolWithdrawn(_stablecoin, _amount, _recipient);
    }

    // Funções administrativas
    function updateStablecoin(
        address _stablecoin,
        uint256 _minAmount,
        uint256 _maxAmount,
        uint256 _dailyLimit,
        uint256 _exchangeRate
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(stablecoins[_stablecoin].isActive, "Stablecoin not supported");
        require(_maxAmount > _minAmount, "Max amount must be greater than min");
        require(_dailyLimit > _maxAmount, "Daily limit must be greater than max");
        require(_exchangeRate > 0, "Exchange rate must be greater than 0");

        StablecoinInfo storage stablecoinInfo = stablecoins[_stablecoin];
        stablecoinInfo.minAmount = _minAmount;
        stablecoinInfo.maxAmount = _maxAmount;
        stablecoinInfo.dailyLimit = _dailyLimit;
        stablecoinInfo.exchangeRate = _exchangeRate;

        emit StablecoinUpdated(_stablecoin, _minAmount, _maxAmount, _dailyLimit, _exchangeRate);
    }

    function updateFeePercentage(uint256 _feePercentage) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_feePercentage <= MAX_FEE_PERCENTAGE, "Fee percentage too high");
        feePercentage = _feePercentage;
    }

    function updateAddresses(
        address _treasury,
        address _feeCollector
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_treasury != address(0), "Invalid treasury address");
        require(_feeCollector != address(0), "Invalid fee collector address");

        treasury = _treasury;
        feeCollector = _feeCollector;
    }

    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    // Funções de visualização
    function getStablecoinInfo(address _stablecoin) external view returns (StablecoinInfo memory) {
        return stablecoins[_stablecoin];
    }

    function getPixTransaction(string calldata _pixId) external view returns (PixTransaction memory) {
        return pixTransactions[_pixId];
    }

    function getUserDailyVolume(address _user) external view returns (uint256) {
        uint256 currentDay = block.timestamp / 1 days;
        return dailyVolume[_user][currentDay];
    }

    function calculateStablecoinAmount(
        uint256 _pixAmount,
        address _stablecoin
    ) external view returns (uint256) {
        return _calculateStablecoinAmount(_pixAmount, _stablecoin);
    }

    function calculatePixAmount(
        uint256 _stablecoinAmount,
        address _stablecoin
    ) external view returns (uint256) {
        return _calculatePixAmount(_stablecoinAmount, _stablecoin);
    }

    function calculateFee(uint256 _amount) external view returns (uint256) {
        return _calculateFee(_amount);
    }

    function getSupportedStablecoins() external view returns (address[] memory) {
        return supportedStablecoins;
    }

    function getPoolBalance(address _stablecoin) external view returns (uint256) {
        return stablecoins[_stablecoin].poolBalance;
    }
}

contracts/tokens/BRLAToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title BRLAToken
 * @dev Token BRLA (Brazilian Real Lastreado) - Stablecoin lastreado em Real
 * @author Felipe Bercio
 */
contract BRLAToken is ERC20, ERC20Burnable, Ownable, Pausable, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // Eventos
    event MinterAdded(address indexed account);
    event MinterRemoved(address indexed account);
    event BurnerAdded(address indexed account);
    event BurnerRemoved(address indexed account);

    constructor(
        string memory name,
        string memory symbol,
        address initialOwner
    ) ERC20(name, symbol) Ownable(initialOwner) {
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(MINTER_ROLE, initialOwner);
        _grantRole(BURNER_ROLE, initialOwner);
        _grantRole(PAUSER_ROLE, initialOwner);
    }

    /**
     * @dev Mint tokens para um endereço específico
     * @param to Endereço de destino
     * @param amount Quantidade a ser mintada
     */
    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) whenNotPaused {
        _mint(to, amount);
    }

    /**
     * @dev Burn tokens de um endereço específico
     * @param from Endereço de origem
     * @param amount Quantidade a ser queimada
     */
    function burnFrom(address from, uint256 amount) public override onlyRole(BURNER_ROLE) whenNotPaused {
        _burn(from, amount);
    }

    /**
     * @dev Pausar o contrato
     */
    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @dev Despausar o contrato
     */
    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @dev Adicionar minter
     */
    function addMinter(address account) public onlyOwner {
        grantRole(MINTER_ROLE, account);
        emit MinterAdded(account);
    }

    /**
     * @dev Remover minter
     */
    function removeMinter(address account) public onlyOwner {
        revokeRole(MINTER_ROLE, account);
        emit MinterRemoved(account);
    }

    /**
     * @dev Adicionar burner
     */
    function addBurner(address account) public onlyOwner {
        grantRole(BURNER_ROLE, account);
        emit BurnerAdded(account);
    }

    /**
     * @dev Remover burner
     */
    function removeBurner(address account) public onlyOwner {
        revokeRole(BURNER_ROLE, account);
        emit BurnerRemoved(account);
    }

    /**
     * @dev Override para incluir pausabilidade
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._update(from, to, amount);
    }

    /**
     * @dev Override necessário para AccessControl
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

